abi.encode(0x22880203d5ab3642);  /* line */ 
        abi.encode(0x0ae10c244961290f);  /* statement */ 
uint256 difference = expectedAmount - actualAmount;
abi.encode(0xd52b1f047a43a193);  /* line */ 
